<div class="infobox-product-horiz">		<? 	$query=mysql_query("SELECT * FROM index_infobox WHERE lang='EN'");		$sorok=mysql_num_rows($query);	$width=round(850/$sorok);	//width in pixel			$i=0;	while ($adatok = mysql_fetch_array($query))	{		if ($adatok['szoveg'])	{				$i++;				if ($i==1) $border="border-left:none;"; else $border='';				if ($i==$sorok) $border.="border-right:none;"; else $border.='';				echo '<a href="'.$adatok['url'].'" style="width:'.$width.'px; '.$border.'">'.$adatok['szoveg'].'</a>';		}	}	?>	</div>