<?//HIBÁK KIÍRATÁSA - facebox div-velif ( (isset($error->error)) || (isset($error->message)) )    {    echo '<div id="hibauzenet" style="display:none;"><p>';    if (count($error->error)>0)    {        while ($hiba = each($error->error))    {                    echo $hiba[1];        }    }    if (count($error->message)>0)    {        while ($uzenet = each($error->message)){                    echo $uzenet[1];        }        }        echo '</p></div>';}