$('#order-form select[name="s"]').change(function () {
    $('#order-form').submit();
});

alertModal = function (title, body) {

    $('#alertModal').modal('show');
    $('#alertModal #alertModalLabel').html(title);
    $('#alertModal .modal-body').html(body);

}

var addNotice = function (message, url) {
    $("html, body").animate({scrollTop: 0}, 200, function () {
        if (url)
            $('.notice').attr('onclick', 'document.location.href="' + url + '"');
        $('.notice').html(message).fadeIn();
    });
}

var refreshCartCount = function () {

    $.ajax({
        method: "GET",
        url: "/cart/get-cart-count",
    }).done(function (result) {
        $('.cart-count').html('(' + result.count + ')');
        if (parseInt(result.count) > 0)
            $('.cart-link').attr('href', $('.cart-link').data('href'));
        else
            $('.cart-link').attr('href', '#');
    });

}

var getCart = function () {

    $.ajax({
        method: "GET",
        url: "/cart/get-cart",
    }).done(function (result) {
        $('.cart-container').html(result);
        $('.cart-container-top').html(result);
    });

}

var deleteCartItem = function (meret) {

    $.ajax({
        method: "POST",
        url: "/cart/ajax-delete-cart-item",
        data: {
            'meret': meret
        }
    }).done(function (result) {
        refreshCartCount();
        getCart();
    });

}

$(document).on("change", 'select[name="quantity"]', function () {

    var prop = $(this).val().split('#');

    $.ajax({
        method: "POST",
        url: "/cart/ajax-add-item",
        data: {
            'meret': prop[1],
            'mennyiseg': prop[0],
            'modify': true,
        }
    }).done(function (result) {

        refreshCartCount();
        getCart();

    });

});

$(document).on("click", '.kupon-btn', function () {

    $.ajax({
        method: "POST",
        url: "/cart/ajax-set-code",
        data: {
            'kupon': $(this).closest('.input-group').find('input.kupon').val(),
        }
    }).done(function (result) {

        getCart();

        if (result.success === true)
            alertModal('Gratulálunk!', 'Az általad megadott kód érvényes!');
        else
            alertModal('Hiba!', 'Az általad megadott kód nem érvényes!');

    });

});

const isMobile = {
    getUserAgent: () => {
    return navigator.userAgent;
},
Android: function() {
    return /Android/i.test(isMobile.getUserAgent()) && !isMobile.Windows();
},
BlackBerry: function() {
    return /BlackBerry|BB10|PlayBook/i.test(isMobile.getUserAgent());;
},
iPhone: function() {
    return /iPhone/i.test(isMobile.getUserAgent()) && !isMobile.iPad() && !isMobile.Windows();
},
iPod: function() {
    return /iPod/i.test(isMobile.getUserAgent());
},
iPad: function() {
    return /iPad/i.test(isMobile.getUserAgent());
},
iOS: function() {
    return (isMobile.iPad() || isMobile.iPod() || isMobile.iPhone());
},
Opera: function() {
    return /Opera Mini/i.test(isMobile.getUserAgent());
},
Windows: function() {
    return /Windows Phone|IEMobile|WPDesktop/i.test(isMobile.getUserAgent());
},
KindleFire: function() {
    return /Kindle Fire|Silk|KFAPWA|KFSOWI|KFJWA|KFJWI|KFAPWI|KFAPWI|KFOT|KFTT|KFTHWI|KFTHWA|KFASWI|KFTBWI|KFMEWI|KFFOWI|KFSAWA|KFSAWI|KFARWI/i.test(isMobile.getUserAgent());
},
any: function() {
    return (isMobile.Android() || isMobile.BlackBerry() || isMobile.iOS() || isMobile.Opera() || isMobile.Windows());
}
};

