  function createSlick(selectedSlider){  

  $(selectedSlider).not('.slick-initialized').slick({
    dots: false,
    slidesToShow: 4,
    slidesToScroll: 1,
    arrows: true,
    nextArrow: '<div class="slick-prev slick-arrow carousel-control-prev" aria-label="Previous"><img src="/images/leftArrow_black.svg" alt=""></div>',
    prevArrow: '<div class="slick-next slick-arrow carousel-control-next" aria-label="Previous"><img src="/images/rightArrow_black.svg" alt=""></div>',
    autoplay: true,
    infinite: true,
    speed: 800,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 2,
          slidesToScroll:2,
          arrows: false
        }
      }

    // You can unslick at a given breakpoint now by adding:
    // settings: "unslick"
    // instead of a settings object
  ]
}); 

}

createSlick(".slider");
createSlick(".slider2");

//Now it will not throw error, even if called multiple times.
$(window).on( 'resize', createSlick );

$('#navbarCollapse').on('show.bs.collapse', function () {
  $('body').addClass("overflow-y");
});

$('#navbarCollapse').on('hide.bs.collapse', function () {
  $('body').removeClass("overflow-y");
});

